"use strict";

const turnLeft = {"^": "<", ">": "^", "v": ">", "<": "v"};
const turnRight = {"^": ">", ">": "v", "v": "<", "<": "^"};
const moves = {"^": [0, -1], ">": [1, 0], "v": [0, 1], "<": [-1, 0]};


function calc() {	
	const robot = new IntCode(input);
	robot.run();

	const map = [];
	let row = [];

	robot.output.forEach(e => {
		if (e != 10) {
			row.push(String.fromCharCode(e));
		} else {
			map.push(row);
			row = [];
		}
	});

	console.log(map.map(l => l.map(c => c == "." ? "_" : c).join("")).join("\n"));

	const intersections = [];
	let xyh = [];

	const getNext = (xyh) => {
		const [x, y, front] = xyh;
		const left = turnLeft[front]; 
		const right = turnRight[front]; 
		const back = turnRight[turnRight[front]];

		const res = [];
		res.push((map[y + moves[front][1]] || [])[x + moves[front][0]]);
		res.push((map[y + moves[left][1]] || [])[x + moves[left][0]]);
		res.push((map[y + moves[right][1]] || [])[x + moves[right][0]]);
		res.push((map[y + moves[back][1]] || [])[x + moves[back][0]]);
		return res;
	}

	for (let y = 0; y < map.length; ++y) {
		for (let x = 0; x < map[y].length; ++x) {
			if (map[y][x] == "#" && getNext([x, y, "^"]).filter(e => e == "#").length > 2) {
				intersections.push([x, y]);
			} else if (map[y][x] == "^" || map[y][x] == "v" || map[y][x] == "<" || map[y][x] == ">") {
				xyh = [x, y, map[y][x]];
				map[y][x] = "#";
			}
		}
	}

	const part1 = intersections.reduce((s, e) => s + e[0] * e[1], 0);

	let xyh1 = [...xyh];

	const seq = [];
	let dist = 0;

	for (;;) {
		const next = getNext(xyh1);
		if (next[0] == "#") {
			++dist;
			xyh1[0] += moves[xyh1[2]][0];
			xyh1[1] += moves[xyh1[2]][1];
		} else {
			if (dist != 0) {
				seq.push((dist).toString());
			}

			dist = 0;

			if (next[1] != "#" && next[2] != "#") {
				break;
			}

			seq.push(next[1] == "#" ? "L" : "R");
			xyh1[2] = next[1] == "#" ? turnLeft[xyh1[2]] : turnRight[xyh1[2]];
		}
	}

	const program = convert(seq.join(",")).split("").map(c => c.charCodeAt(0));

	const robot2 = new IntCode(input, program);
	robot2.set(0, 0, 2);
	robot2.run();

	return part1 + " " + robot2.output.pop();
}

function convert(seq) {
	if (seq != "L,10,R,10,L,10,L,10,R,10,R,12,L,12,L,10,R,10,L,10,L,10,R,10,R,12,L,12,R,12,L,12,R,6,R,12,L,12,R,6,R,10,R,12,L,12,L,10,R,10,L,10,L,10,R,10,R,12,L,12,R,12,L,12,R,6") {
		throw "Sorry, it is not solved for your input.";
	}

	// solved by hand :(

	return `A,B,A,B,C,C,B,A,B,C
L,10,R,10,L,10,L,10
R,10,R,12,L,12
R,12,L,12,R,6
n
`;
}

function IntCode(text, input) {
	const m = text.split(",").map(Number).reduce((o, e, i) => (o[i] = e, o), {});
	let ip = 0;
	let base = 0;

	this.input = input || [];
	this.output = [];
	this.running = true;

	const opAdd = 1;
	const opMul = 2;
	const opIn = 3;
	const opOut = 4;
	const opJumpIfTrue = 5;
	const opJumpIfFalse = 6;
	const opLessThan = 7;
	const opEquals = 8;
	const opRelBase = 9;
	const opHalt = 99;

	this.get = (a, mode) => {
		if (mode == 0) {
			return m[a] || 0;
		} else if (mode == 1) {
			return a;
		} else if (mode == 2) {
			return m[a + base] || 0;
		} else {
			return 0;
		}
	}

	this.set = (a, mode, value) => {
		if (mode == 0) {
			m[a] = value;
		} else if (mode == 2) {
			m[a + base] = value;
		}
	}

	this.run = () => {
		for (;;) {
			const [op, x, y, z] = [m[ip] || 0, m[ip + 1] || 0, m[ip + 2] || 0, m[ip + 3] || 0];
			const opcode = op % 100;
			const modeX = Math.floor(op / 100) % 10;
			const modeY = Math.floor(op / 1000) % 10;
			const modeZ = Math.floor(op / 10000) % 10;
			
			if (opcode == opAdd) {
				this.set(z, modeZ, this.get(x, modeX) + this.get(y, modeY));
				ip += 4;

			} else if (opcode == opMul) {
				this.set(z, modeZ, this.get(x, modeX) * this.get(y, modeY));
				ip += 4;

			} else if (opcode == opIn) {
				if (this.input.length == 0) {
					break;
				}

				this.set(x, modeX, this.input.shift());
				ip += 2;

			} else if (opcode == opOut) {
				this.output.push(this.get(x, modeX));
				ip += 2;

			} else if (opcode == opJumpIfTrue) {;
				if (this.get(x, modeX) != 0) {
					ip = this.get(y, modeY);
				} else {
					ip += 3;
				}

			} else if (opcode == opJumpIfFalse) {
				if (this.get(x, modeX) == 0) {
					ip = this.get(y, modeY);
				} else {
					ip += 3;
				}

			} else if (opcode == opLessThan) {
				this.set(z, modeZ, this.get(x, modeX) < this.get(y, modeY) ? 1 : 0);
				ip += 4;

			} else if (opcode == opEquals) {
				this.set(z, modeZ, this.get(x, modeX) == this.get(y, modeY) ? 1 : 0);
				ip += 4;

			} else if (opcode == opRelBase) {
				base += this.get(x, modeX);
				ip += 2;

			} else if (opcode == opHalt) {
				this.running = false;
				break;

			} else {
				ip += 1;	// invalid opcode
			}
		}
	}
}

